import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useData } from '../../context/DataContext';
import { addDays, format, startOfWeek, endOfWeek, startOfMonth, endOfMonth, eachDayOfInterval, isSameMonth, isSameDay, parseISO } from 'date-fns';
import { es } from 'date-fns/locale';
import { ChevronLeft, ChevronRight, Calendar as CalendarIcon, List, LayoutGrid, CheckSquare, Square, PenLine } from 'lucide-react';

const TrackingCalendar = () => {
    const { patients, reviews, saveReview } = useData();
    const navigate = useNavigate();
    const [currentDate, setCurrentDate] = useState(new Date());
    const [viewMode, setViewMode] = useState('week'); // Default to 'week'

    // Generate Review Events
    const events = patients.flatMap(patient => {
        if (!patient.subscription?.startDate) return [];

        const start = parseISO(patient.subscription.startDate);
        const end = patient.subscription.endDate ? parseISO(patient.subscription.endDate) : addDays(start, 30); // Default or actual end
        const patientEvents = [];

        // Generate weekly reviews from start date
        let reviewDate = addDays(start, 7);
        while (reviewDate <= end) {
            const reviewId = `review_${patient.id}_${format(reviewDate, 'yyyy-MM-dd')}`;
            const savedData = reviews?.[reviewId] || {};

            patientEvents.push({
                sysId: reviewId,
                date: reviewDate,
                patientName: patient.name,
                patientId: patient.id,
                type: 'Revisión',
                completed: savedData.completed || false,
                notes: savedData.notes || ''
            });
            reviewDate = addDays(reviewDate, 7);
        }
        return patientEvents;
    });

    // Toggle Complete
    const handleToggleComplete = (evt) => {
        saveReview(evt.sysId, { completed: !evt.completed });
    };

    // Save Notes
    const handleNotesChange = (sysId, note) => {
        saveReview(sysId, { notes: note });
    };

    // Calendar Navigation
    const nextPeriod = () => {
        if (viewMode === 'week' || viewMode === 'list') {
            setCurrentDate(addDays(currentDate, 7));
        } else {
            const next = new Date(currentDate);
            next.setMonth(currentDate.getMonth() + 1);
            setCurrentDate(next);
        }
    };

    const prevPeriod = () => {
        if (viewMode === 'week' || viewMode === 'list') {
            setCurrentDate(addDays(currentDate, -7));
        } else {
            const prev = new Date(currentDate);
            prev.setMonth(currentDate.getMonth() - 1);
            setCurrentDate(prev);
        }
    };

    // View Logic Dates
    const monthStart = startOfMonth(currentDate);
    const monthEnd = endOfMonth(monthStart);
    const monthStartDate = startOfWeek(monthStart, { weekStartsOn: 1 });
    const monthEndDate = endOfWeek(monthEnd, { weekStartsOn: 1 });

    const weekStart = startOfWeek(currentDate, { weekStartsOn: 1 });

    // Determine days to render based on viewMode
    const calendarDays = viewMode === 'week'
        ? eachDayOfInterval({ start: weekStart, end: endOfWeek(currentDate, { weekStartsOn: 1 }) })
        : eachDayOfInterval({ start: monthStartDate, end: monthEndDate });

    const weekDays = ['Lunes', 'Martes', 'Miércoles', 'Jueves', 'Viernes', 'Sábado', 'Domingo'];

    // List View Grouping
    const sortedEvents = [...events].sort((a, b) => a.date - b.date);

    // Filter events for List View: Only show events for the CURRENT selected week (defined by weekStart and weekEnd)
    const validListEvents = sortedEvents.filter(evt => {
        return evt.date >= weekStart && evt.date <= endOfWeek(currentDate, { weekStartsOn: 1 });
    });

    const eventsByWeek = validListEvents.reduce((acc, event) => {
        const weekKey = `${format(startOfWeek(event.date, { weekStartsOn: 1 }), 'yyyy-MM-dd')}`;
        if (!acc[weekKey]) acc[weekKey] = [];
        acc[weekKey].push(event);
        return acc;
    }, {});

    const headerTitle = (viewMode === 'week' || viewMode === 'list')
        ? `Semana del ${format(weekStart, 'd MMM', { locale: es })} al ${format(endOfWeek(currentDate, { weekStartsOn: 1 }), 'd MMM', { locale: es })}`
        : format(currentDate, 'MMMM yyyy', { locale: es });

    return (
        <div className="dashboard-container">
            <div className="dashboard-header">
                <div>
                    <h1 className="page-title">Calendario de Seguimiento</h1>
                    <p className="page-subtitle">Revisiones programadas de clientes</p>
                </div>
                <div className="flex bg-white border border-slate-200 shadow-sm p-1 rounded-lg">
                    <button
                        onClick={() => setViewMode('list')}
                        className={`p-2 rounded-md transition-colors flex gap-2 items-center text-sm font-medium ${viewMode === 'list' ? 'bg-slate-100 text-slate-900' : 'text-slate-400 hover:text-slate-600'}`}
                    >
                        <List size={18} /> Lista
                    </button>
                    <button
                        onClick={() => setViewMode('week')}
                        className={`p-2 rounded-md transition-colors flex gap-2 items-center text-sm font-medium ${viewMode === 'week' ? 'bg-slate-100 text-slate-900' : 'text-slate-400 hover:text-slate-600'}`}
                    >
                        <LayoutGrid size={18} /> Semanal
                    </button>
                    <button
                        onClick={() => setViewMode('month')}
                        className={`p-2 rounded-md transition-colors flex gap-2 items-center text-sm font-medium ${viewMode === 'month' ? 'bg-slate-100 text-slate-900' : 'text-slate-400 hover:text-slate-600'}`}
                    >
                        <CalendarIcon size={18} /> Mensual
                    </button>
                </div>
            </div>

            {viewMode !== 'list' ? (
                <div className="card">
                    {/* Header */}
                    <div className="flex items-center justify-between mb-8">
                        <div className="flex items-center gap-4">
                            <h2 className="text-xl font-bold text-slate-800 capitalize">
                                {headerTitle}
                            </h2>
                            <div className="flex gap-1">
                                <button onClick={prevPeriod} className="btn btn-sm btn-outline px-2 border-slate-200">
                                    <ChevronLeft size={16} />
                                </button>
                                <button onClick={() => setCurrentDate(new Date())} className="btn btn-sm btn-outline border-slate-200">
                                    Hoy
                                </button>
                                <button onClick={nextPeriod} className="btn btn-sm btn-outline px-2 border-slate-200">
                                    <ChevronRight size={16} />
                                </button>
                            </div>
                        </div>
                    </div>

                    {/* Grid */}
                    <div className="grid grid-cols-7 mb-2">
                        {weekDays.map(day => (
                            <div key={day} className="text-center text-sm font-bold text-slate-400 uppercase tracking-wider py-2">
                                {day.substring(0, 3)}
                            </div>
                        ))}
                    </div>

                    <div className="grid grid-cols-7 gap-px bg-slate-200 border border-slate-200 rounded-lg overflow-hidden">
                        {calendarDays.map((day) => {
                            const dayEvents = events.filter(e => isSameDay(e.date, day));
                            const isCurrentMonth = isSameMonth(day, monthStart); // Only relevant for month view styling
                            const isToday = isSameDay(day, new Date());

                            // Adjust styling for week view vs month view
                            const opacityClass = (viewMode === 'month' && !isCurrentMonth) ? 'bg-slate-50/50 text-slate-400' : '';
                            const minHeightClass = viewMode === 'week' ? 'min-h-[400px]' : 'min-h-[120px]';

                            return (
                                <div
                                    key={day.toString()}
                                    className={`${minHeightClass} bg-white p-2 transition-colors hover:bg-slate-50 relative group ${opacityClass}`}
                                >
                                    <div className={`text-right text-sm font-medium mb-1 ${isToday ? 'bg-primary-600 text-white w-7 h-7 rounded-full flex items-center justify-center ml-auto shadow-sm' : ''}`}>
                                        {format(day, 'd')}
                                    </div>

                                    <div className="space-y-1 overflow-y-auto max-h-[350px] scrollbar-thin">
                                        {dayEvents.map((evt, i) => (
                                            <div
                                                key={i}
                                                className={`text-xs p-1.5 rounded border flex items-center gap-1.5 truncate shadow-sm transition-all group/evt
                                                    ${evt.completed
                                                        ? 'bg-green-50 text-green-700 border-green-200 opacity-75'
                                                        : 'bg-primary-50 text-primary-700 border-primary-100 hover:bg-primary-100'
                                                    }
                                                `}
                                            >
                                                <button
                                                    onClick={(e) => { e.stopPropagation(); handleToggleComplete(evt); }}
                                                    className="flex-shrink-0 text-current hover:scale-110 transition-transform"
                                                    title={evt.completed ? "Marcar como pendiente" : "Marcar como hecho"}
                                                >
                                                    {evt.completed ? <CheckSquare size={12} /> : <Square size={12} />}
                                                </button>
                                                <span
                                                    onClick={() => navigate(`/patients/${evt.patientId}`)}
                                                    className={`font-medium truncate cursor-pointer hover:underline flex-1 ${evt.completed ? 'line-through' : ''}`}
                                                    title={`Ver detalle de ${evt.patientName}`}
                                                >
                                                    {evt.patientName}
                                                </span>
                                            </div>
                                        ))}
                                    </div>
                                </div>
                            );
                        })}
                    </div>
                </div>
            ) : (
                /* List View grouped by Week */
                <div className="space-y-8">
                    {/* List View Navigation Header - Copied from Calendar View logic */}
                    <div className="flex items-center justify-between mb-4">
                        <div className="flex items-center gap-4">
                            <h2 className="text-xl font-bold text-slate-800 capitalize">
                                {headerTitle}
                            </h2>
                            <div className="flex gap-1">
                                <button onClick={prevPeriod} className="btn btn-sm btn-outline px-2 border-slate-200">
                                    <ChevronLeft size={16} />
                                </button>
                                <button onClick={() => setCurrentDate(new Date())} className="btn btn-sm btn-outline border-slate-200">
                                    Hoy
                                </button>
                                <button onClick={nextPeriod} className="btn btn-sm btn-outline px-2 border-slate-200">
                                    <ChevronRight size={16} />
                                </button>
                            </div>
                        </div>
                    </div>

                    {Object.keys(eventsByWeek).length === 0 ? (
                        <div className="card text-center py-10 text-slate-500">No hay revisiones programadas para esta semana.</div>
                    ) : Object.keys(eventsByWeek).sort().map(weekKey => {
                        // In list view strictly by week, this map will only run once or zero times
                        const weekStart = new Date(weekKey);
                        // const weekEnd = endOfWeek(weekStart, { weekStartsOn: 1 }); // Unused
                        const weekEvents = eventsByWeek[weekKey];

                        return (
                            <div key={weekKey} className="card">
                                {/* Removed redundant week header since we have global navigation now,
                                     but keeping simple header or removing completely?
                                     Let's keep the card structure but remove the title if it's redundant
                                     or keep it cleaner. Let's keep it simple. */}
                                <div className="table-responsive">
                                    <table className="data-table">
                                        <thead>
                                            <tr>
                                                <th className="w-12 text-center">Estado</th>
                                                <th className="w-32">Fecha</th>
                                                <th className="w-48">Cliente</th>
                                                <th>Cambios a Realizar / Notas</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {weekEvents.map(evt => (
                                                <tr key={evt.sysId} className={evt.completed ? 'bg-slate-50' : 'hover:bg-primary-50'}>
                                                    <td className="text-center">
                                                        <button
                                                            onClick={() => handleToggleComplete(evt)}
                                                            className={`p-1 rounded transition-colors ${evt.completed ? 'text-green-600 hover:text-green-700' : 'text-slate-300 hover:text-slate-500'}`}
                                                            title={evt.completed ? "Marcar como pendiente" : "Marcar como hecho"}
                                                        >
                                                            {evt.completed ? <CheckSquare size={20} /> : <Square size={20} />}
                                                        </button>
                                                    </td>
                                                    <td className={`font-medium text-sm ${evt.completed ? 'text-slate-400 line-through' : 'text-slate-600'}`}>
                                                        {format(evt.date, 'EEEE d', { locale: es })}
                                                    </td>
                                                    <td>
                                                        <span
                                                            onClick={() => navigate(`/patients/${evt.patientId}`)}
                                                            className={`font-semibold cursor-pointer hover:text-primary transition-colors ${evt.completed ? 'text-slate-400 line-through' : 'text-slate-700'}`}
                                                        >
                                                            {evt.patientName}
                                                        </span>
                                                    </td>
                                                    <td>
                                                        <div className="relative group w-full">
                                                            <input
                                                                type="text"
                                                                defaultValue={evt.notes}
                                                                onBlur={(e) => handleNotesChange(evt.sysId, e.target.value)}
                                                                onKeyDown={(e) => {
                                                                    if (e.key === 'Enter') {
                                                                        e.currentTarget.blur();
                                                                    }
                                                                }}
                                                                className={`w-full bg-transparent border-b border-transparent hover:border-slate-200 focus:border-primary-400 outline-none py-1.5 px-2 text-sm transition-all ${evt.completed ? 'text-slate-400' : 'text-slate-700'}`}
                                                                placeholder="Escribe cambios o notas..."
                                                            />
                                                            <PenLine size={14} className="absolute right-2 top-1/2 -translate-y-1/2 text-slate-300 opacity-0 group-hover:opacity-100 transition-opacity pointer-events-none" />
                                                        </div>
                                                    </td>
                                                </tr>
                                            ))}
                                        </tbody>
                                    </table>
                                </div>
                            </div>
                        );
                    })}
                </div>
            )}
        </div>
    );
};

export default TrackingCalendar;
